mapper.select:
SELECT info.adminId, info.usercode, info.username, info.landlordtype,info.landlordtypename, info.tel, info.gendar, info.authName, info.paperstype,info.papersnum,info.photo, info.papersphoto,info.isapp AS appOpen, info.registerpeople AS regPerson, bind.num AS buildingNum FROM ( select id as adminId, usercode,username, tel, papersnum,photo,papersphoto, registerpeople, landlordtype, case paperstype WHEN 0 THEN "身份证" WHEN 1 THEN "港澳通行证" end paperstype, case isapp WHEN 0 THEN "是" WHEN 1 THEN "否" end isapp, case landlordtype WHEN 0 THEN "未知" WHEN 1 THEN "原房东" WHEN 2 THEN "管理员" WHEN 3 THEN "房东家属" WHEN 4 THEN "网格员" WHEN 5 THEN "警务人员" WHEN 6 THEN "治安科" WHEN 72 THEN "保洁人员" WHEN 8 THEN "代管" WHEN 9 THEN "维修人员" WHEN 10 THEN "申报义务人" WHEN 11 THEN "送气人员" WHEN 12 THEN "集成单位" END landlordtypename , CASE usersex WHEN 0 THEN "男" WHEN 1 THEN "女" WHEN 2 THEN "未知" END gendar, CASE adminjurisdiction WHEN 0 THEN "所有权限" WHEN 1 THEN "只读权限" WHEN 2 THEN "无权限" END authName FROM base_landlord WHERE usercode IN ( SELECT usercode FROM admin_building_bind WHERE build_code = #{code}
AND user_type = #{type}
) ) info, ( SELECT usercode, COUNT(0) AS num FROM admin_building_bind bind WHERE usercode IN ( SELECT usercode FROM admin_building_bind WHERE build_code = #{code}
AND user_type = #{type}
) GROUP BY usercode ) bind WHERE info.usercode = bind.usercode

mapper.delete:
delete from admin_building_bind
where usercode = #{usercode}
and id in

    #{item}
