mapper.select:
SELECT
id,
logic_cardno,
physical_cardno,
usercode,
username,
phone,
admincode,
buildcode,
buildname,
housecode,
isalive,
createtime,
sn,
cardtype,
cardkind,
remark
FROM
base_card

    1=1
    AND id = #{id}
    AND logic_cardno = #{logic_cardno}
    AND physical_cardno =
        #{physical_cardno}

    AND usercode = #{usercode}
    AND username = #{username}
    AND phone = #{phone}
    AND admincode = #{admincode}
    AND buildcode = #{buildcode}
    AND buildname = #{buildname}
    AND housecode = #{housecode}
    AND isalive = #{isalive}
    AND createtime = #{createtime}
    AND sn = #{sn}
    AND cardtype = #{cardtype}
    AND cardkind = #{cardkind}
    AND remark = #{remark}

mapper.update:
update base_card
set isalive = #{isalive}
where logic_cardno = #{cardNo}

UPDATE base_lessee
headphoto = #{req.headphoto,jdbcType=VARCHAR}, photo = #{req.photo,jdbcType=VARCHAR}, papersphoto = #{req.papersphoto,jdbcType=VARCHAR},
